INSERT INTO `tbl_news` (`id`, `name`, `content`, `author`, `date`, `active`, `image`) VALUES
(1, 'Máy lọc nước gia đình nào tốt nhất hiện nay', '<p><strong><span style="font-size:14px">M&Aacute;Y LỌC NƯỚC GIA ĐÌNH N&Agrave;O TỐT NHẤT HIỆN NAY ?</span></strong></p>\r\n\r\n<h2><span style="font-size:14px">Kh&ocirc;ng phải ai cũng biết c&aacute;ch lựa chọn một chiếc m&aacute;y m&aacute;y lọc nước gia đ&igrave;nh tốt nhất. Vậy đ&acirc;u l&agrave; những ti&ecirc;u chuẩn gi&uacute;p ch&uacute;ng ta chọn được một chiếc m&aacute;y lọc nước ph&ugrave; hợp?</span></h2>\r\n\r\n<p><span style="font-size:14px">Nước chiếm 75% cơ thể v&agrave; đ&oacute;ng g&oacute;p thiết thực v&agrave;o nguồn dinh dưỡng của con người. Do đ&oacute;, chất lượng nước th&igrave; phải lu&ocirc;n được bảo đảm. Nhưng với mật độ c&ocirc;ng nghiệp h&oacute;a v&agrave; &ocirc; nhiễm m&ocirc;i trường, đặc biệt ở th&agrave;nh phố th&igrave; vấn đề c&oacute; nước sạch lu&ocirc;n được quan t&acirc;m. Việc sử dụng&nbsp;m&aacute;y lọc nước&nbsp;để c&oacute; nguồn nước sạch cho việc ăn uống, sinh hoạt đ&atilde; trở l&ecirc;n phổ biến v&agrave; th&ocirc;ng dụng l&agrave; điều tất yếu.</span></p>\r\n\r\n<p><span style="font-size:14px">Tuy nhi&ecirc;n, kh&ocirc;ng phải ai cũng biết lựa chọn cho gia đ&igrave;nh m&igrave;nh một chiếc m&aacute;y lọc nước ph&ugrave; hợp. Vậy đ&acirc;u l&agrave; ti&ecirc;u chuẩn ?</span></p>\r\n\r\n<p><span style="font-size:14px"><img alt="" src="/upload/ckfinder/images/News/07-16-2015/may-loc-nuoc-gia-dinh.jpg" style="height:324px; width:960px" /></span></p>\r\n\r\n<ol>\r\n	<li>\r\n	<h3><span style="font-size:14px">Chất lượng m&aacute;y lọc nước</span></h3>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style="font-size:14px">T&ugrave;y thuộc v&agrave;o vấn đề về nước gặp phải, bạn c&oacute; thể lựa chọn cho m&igrave;nh những kiểu&nbsp;<strong><em>m&aacute;y lọc nước gia đ&igrave;nh</em></strong>ph&ugrave; hợp. Đối với nước giếng khoan hoặc nguồn nước m&aacute;y c&oacute; độ &ocirc; nhiễm cao, nước nhiễm lợ, m&aacute;y lọc nước RO sẽ khắc phục được những nhược điểm n&agrave;y. Tuy nhi&ecirc;n với nguồn nước l&agrave; nước mưa c&oacute; t&iacute;nh axit cao qua m&aacute;y lọc RO sẽ l&agrave;m tăng t&iacute;nh axit của nước, c&oacute; hại cho sức khỏe. Thay v&agrave;o đ&oacute;, h&atilde;y sử dụng&nbsp;<em><strong>m&aacute;y lọc nước Nano</strong></em>&nbsp;c&oacute; chức năng trung h&ograve;a axit, sẽ khắc phục được nhược điểm n&agrave;y.</span></p>\r\n\r\n<p><span style="font-size:14px"><img alt="" src="/upload/ckfinder/images/News/07-16-2015/may-loc-nuoc-kd5.png" style="height:300px; width:600px" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-size:14px">C&oacute; một thực tế c&oacute; nhiều kh&aacute;ch h&agrave;ng chọn sai&nbsp;<strong>m&aacute;y lọc nước gia đ&igrave;nh</strong>, than phiền nh&agrave; cung cấp m&aacute;y kh&ocirc;ng tốt, do bạn đ&atilde; chọn sai thiết bị ngay từ đầu. V&iacute; dụ như:</span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">C&oacute; bạn c&oacute; nguồn nước m&aacute;y kh&aacute; tốt của TP H&agrave; Nội, khi mua chọn m&aacute;y lọc nước m&aacute;y RO, trong qu&aacute; tr&igrave;nh sử dụng thấy nước thải nhiều, phải d&ugrave;ng điện cho m&aacute;y bơm, khi mất điện kh&ocirc;ng lấy được nước d&ugrave;ng, nước thải nhiều, chi ph&iacute; thay l&otilde;i lọc cao v&agrave; thường xuy&ecirc;n, thấy bất tiện trong qu&aacute; tr&igrave;nh sử dụng.Trong trường hợp n&agrave;y bạn n&ecirc;n chọn mua&nbsp;<em><strong>m&aacute;y lọc nước Nano</strong></em>, vừa giữ được vi lượng kho&aacute;ng tốt cho sức khỏe, vừa kh&ocirc;ng d&ugrave;ng điện, kh&ocirc;ng nước thải, l&otilde;i lọc bền&hellip;</span></li>\r\n	<li><span style="font-size:14px">C&oacute; bạn d&ugrave;ng nguồn nước giếng khoan, bơm qua bể lọc th&ocirc; sơ s&agrave;i, nước c&oacute; m&agrave;u v&agrave;ng hữu cơ, nhiều cặn sắt, &hellip; Kh&ocirc;ng n&ecirc;n chọn mua m&aacute;y lọc nước Nano để lọc trực tiếp, trong trường hợp n&agrave;y n&ecirc;n chọn m&aacute;y lọc RO, kh&ocirc;ng n&ecirc;n chọn m&aacute;y lọc nước Nano v&igrave; nguồn nước đầu v&agrave;o kh&ocirc;ng đạt ti&ecirc;u chuẩn, nếu d&ugrave;ng m&aacute;y Nano bạn phải l&agrave;m lại bể lọc th&ocirc; theo ti&ecirc;u chuẩn.Qui tr&igrave;nh lọc nước giếng khoan phải tu&acirc;n theo qui tr&igrave;nh sau: Nguồn nước giếng &mdash; Thiết bị lọc nước ( Bể lọc nước) &mdash; Nước sinh hoạt ( Nước m&aacute;y) &mdash; M&aacute;y lọc nước RO, Nano &mdash; Nước uống liền.</span></li>\r\n</ul>\r\n\r\n<ol start="2">\r\n	<li>\r\n	<h3><span style="font-size:14px">Mẫu m&atilde;:</span></h3>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style="font-size:14px">Điều n&agrave;y kh&aacute; quan trọng v&agrave; được nhiều kh&aacute;ch h&agrave;ng c&acirc;n nhắc khi lựa chọn sản phẩm, nhất l&agrave; đối với những căn hộ c&oacute; diện t&iacute;ch nhỏ cần tiếp kiệm kh&ocirc;ng gian.</span></p>\r\n\r\n<p><span style="font-size:14px"><img alt="" src="/upload/ckfinder/images/News/07-16-2015/huong-dan-su-dung-may-loc-nuoc-kangaroo.jpg" style="height:709px; width:551px" /></span></p>\r\n\r\n<p><span style="font-size:14px">Ngo&agrave;i ra, h&igrave;nh d&aacute;ng của m&aacute;y lọc cũng phải ph&ugrave; hợp với c&aacute;ch bố tr&iacute; nội thất trong ph&ograve;ng nhằm tăng t&iacute;nh thẩm mỹ v&agrave; phong thủy.</span></p>\r\n\r\n<ol start="3">\r\n	<li>\r\n	<h3><span style="font-size:14px">Chi ph&iacute; mua m&aacute;y:</span></h3>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style="font-size:14px">Ở đ&acirc;y, t&ocirc;i muốn đề cập đến chi ph&iacute; mua v&agrave; sử dụng m&aacute;y. Trước khi lựa chọn mua&nbsp;<strong>m&aacute;y lọc nước gia đ&igrave;nh</strong>, bạn cần c&acirc;n nhắc xem bao nhi&ecirc;u l&acirc;u cần thay l&otilde;i lọc?, loại m&aacute;y n&agrave;y c&oacute; tốn điện kh&ocirc;ng ? Hay lượng nước thải ra c&oacute; nhiều kh&ocirc;ng ? Để so s&aacute;nh giữa những loại m&aacute;y lọc nước kh&aacute;c nhau.</span></p>\r\n\r\n<p><span style="font-size:14px">Để gi&uacute;p bạn chọn lựa m&aacute;y ph&ugrave; hợp nhất cho gia đ&igrave;nh, đừng ngần ngại h&atilde;y li&ecirc;n hệ ngay với nh&acirc;n vi&ecirc;n tư vấn, dựa v&agrave;o yếu tố nơi sinh hoạt, nguồn nước v&agrave; điều kiện kinh tế sẽ gi&uacute;p bạn c&oacute; được một chiếc m&aacute;y lọc nước ph&ugrave; hợp nhất.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Hotline: 0904 501 820</strong></span></p>\r\n', 'admin', '2015-07-16 10:37:13', 1, 'may-loc-nuoc-kangaroo_uu-diem.jpg'),
(2, 'Uống nước như thế nào là khoa học', '<p><span style="font-size:14px"><span style="color:rgb(85, 85, 85); font-family:arial,arial,helvetica,sans-serif">Như c&aacute;c bạn cũng đ&atilde; biết nước l&agrave; một trong những loại nước uống kh&ocirc;ng thể thiếu trong cuộc sống hằng ng&agrave;y của ch&uacute;ng ta, n&oacute; chiếm 70% trong cở thể, nhưng kh&ocirc;ng v&igrave; thế m&agrave; bạn c&oacute; thể tự nhi&ecirc;n cung cấp v&agrave;o cơ thể của m&igrave;nh bao nhi&ecirc;u nước cũng được, bạn cần phải c&oacute; được cho m&igrave;nh c&aacute;ch uống nước như thế n&agrave;o l&agrave; khoa học nhất gi&uacute;p bạn vẫn đảm bảo được uống đủ số nước m&agrave; vẫn đảm bảo sức khỏe. Ngay sau đ&acirc;y ch&uacute;ng t&ocirc;i sẽ gi&uacute;p bạn l&agrave;m sao uống nước một c&aacute;ch khao học nhất, mời c&aacute;c bạn c&ugrave;ng t&igrave;m hiểu nh&eacute;.</span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:rgb(85, 85, 85); font-family:arial,arial,helvetica,sans-serif"><img alt="" src="/upload/ckfinder/images/News/07-16-2015/child-vater.jpg" style="height:308px; width:566px" /></span></span></p>\r\n\r\n<p><strong><span style="font-size:14px">Những c&aacute;ch uống nước khoa học nhất</span></strong></p>\r\n\r\n<p><span style="font-size:14px">Như đ&atilde; n&oacute;i ở tr&ecirc;n t&ocirc;i sẽ gửi đến bạn những c&aacute;ch uống nước khoa học nhất để bạn c&oacute; thể y&ecirc;n t&acirc;m cũng cấp v&agrave;o cơ thể đủ nước v&agrave; uống l&agrave;m sao mang đến hiệu quả tốt nhất cho sức khỏe. Gồm những lưu &yacute; ngay sau đ&acirc;y.</span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:rgb(255, 0, 0)">Buổi s&aacute;ng</span>&nbsp;l&agrave; ng&agrave;y khởi đầu của bạn để bước v&agrave;o một ng&agrave;y l&agrave;m việc, vui chơi, học tập bạn n&ecirc;n uống 1-2 cốc nước đun s&ocirc;i để nguội c&ograve;n &acirc;m ấm. Đối với người cao tuổi phương ph&aacute;p bảo vệ sức khỏe th&igrave; uống nước như vậy gi&uacute;p người bị tăng huyết &aacute;p, n&oacute; l&agrave;m m&aacute;u lo&atilde;ng ra hơn, l&agrave;m giảm v&agrave; ngăn chặn nguy cơ tắc mạch m&aacute;u n&atilde;o, động mạch v&agrave;nh. Bạn n&ecirc;n thường xuy&ecirc;n uống nước buổi s&aacute;ng để đảm bảo sức khỏe. Nếu trong qu&aacute; tr&igrave;nh ăn s&aacute;ng bạn c&oacute; thể kết hợp những m&oacute;n ăn c&oacute; nhiều nước như&nbsp;nước canh g&agrave;,phở g&agrave;, ch&aacute;o g&agrave;. C&oacute; t&aacute;c dụng&nbsp;th&uacute;c đẩy qu&aacute; tr&igrave;nh b&agrave;i tiết hormon tuyến thượng thận, l&agrave;m phấn chấn tinh thần, tăng cường sức lực, xua đi hết mệt mỏi, căng thẳng, gi&uacute;p bạn c&oacute; được cho m&igrave;nh một ng&agrave;y l&agrave;m việc hiệu quả.</span></p>\r\n\r\n<p><span style="font-size:14px"><span style="color:rgb(255, 0, 0)">Buổi&nbsp;trưa</span>&nbsp;thường th&igrave; mọi người thường bận rộn hơn trong c&ocirc;ng việc, vui chơi, học tập n&ecirc;n bổ sung th&ecirc;m nước đun s&ocirc;i để nguội hoặc nước giải kh&aacute;t kh&ocirc;ng c&oacute; ga. N&ecirc;n uống đều đặn từ 30-45 ph&uacute;t v&agrave;i ba ngụm hoặc nhiều hơn t&ugrave;y theo nhu cầu. Buổi trưa thường th&igrave; mọi người thường bận rộn hơn trong c&ocirc;ng việc, vui chơi, học tập n&ecirc;n bổ sung th&ecirc;m nước đun s&ocirc;i để nguội hoặc nước giải kh&aacute;t kh&ocirc;ng c&oacute; ga. N&ecirc;n uống đều đặn từ 30-45 ph&uacute;t v&agrave;i ba ngụm hoặc nhiều hơn t&ugrave;y theo nhu cầu. Thức ăn của buổi trưa bạn c&oacute; thể uống nước canh rau xanh l&agrave; ch&iacute;nh, những chất như vậy cung cấp cho bạn nhiều vitamin v&agrave; kho&aacute;ng chất gi&uacute;p ph&ograve;ng bệnh hiệu quả.</span></p>\r\n\r\n<p><span style="font-size:14px">Cuối c&ugrave;ng l&agrave; đi ngủ bạn cần uống nước như thế n&agrave;o l&agrave; tốt nhất? để tr&aacute;nh mất ngủ bạn uống nước chỉ chống kh&aacute;t, kh&ocirc;ng n&ecirc;n uống nhiều. Nếu c&oacute; điều kiện bạn c&oacute; thể d&ugrave;ng sữa b&ograve; gi&uacute;p bạn ngủ ngon hơn m&agrave; c&ograve;n bổ sung cho bạn dinh dưỡng canxi, chống trạng th&aacute;i đ&oacute;i canxi v&agrave;o ban đ&ecirc;m m&agrave; c&ograve;n c&oacute; t&aacute;c dụng chống lo&atilde;ng xương v&agrave; tho&aacute;i h&oacute;a khớp.</span></p>\r\n\r\n<p><span style="font-size:14px"><img alt="" src="/upload/ckfinder/images/News/07-16-2015/drinking-water-danger.jpg" style="height:533px; width:800px" /></span></p>\r\n\r\n<h3><strong><span style="font-size:14px">Uống nước thể n&agrave;o l&agrave; tốt nhất</span></strong></h3>\r\n\r\n<p><span style="font-size:14px">Tiếp đến t&ocirc;i sẽ gửi đến bạn nguồn nước bổ sung nhứ thế n&agrave;o v&agrave;o c&aacute;c m&ugrave;a, khi vận động, đối với người gi&agrave;, trẻ em, như thế n&agrave;o l&agrave; tốt nhất.</span></p>\r\n\r\n<p><span style="font-size:14px">V&agrave;o m&ugrave;a h&egrave;, nhu cầu bổ sung nước cho cơ thể cao hơn r&otilde; rệt. N&ecirc;n uống nước ch&egrave; n&oacute;ng. Điều th&uacute; vị l&agrave; trong nước ch&egrave; n&oacute;ng gi&agrave;u chất kali, c&oacute; thể b&ugrave; đắp lượng kali bị b&agrave;i tiết qua mồ h&ocirc;i, l&agrave;m giảm mệt mỏi, trạng th&aacute;i buồn ngủ do thiếu kali. Đồng thời ch&egrave; n&oacute;ng c&ograve;n c&oacute; t&aacute;c dụng giải nhiệt tốt, l&agrave;m giảm nhiệt độ ở da, cho ch&uacute;ng ta cảm gi&aacute;c m&aacute;t mẻ, dễ chịu v&agrave; sảng kho&aacute;i. Trong tất cả c&aacute;c loại ch&egrave;, tốt nhất l&agrave; ch&egrave; xanh (ch&egrave; tươi). Trong th&agrave;nh phần của ch&egrave; xanh c&oacute; khoảng 80 nguy&ecirc;n tố kh&aacute;c nhau cần thiết cho cơ thể. Ch&egrave; xanh c&oacute; t&aacute;c dụng l&agrave;m chắc răng, đ&agrave;o thải chất độc, l&agrave;m mịn da.</span></p>\r\n\r\n<p><span style="font-size:14px">Đối với trẻ em , trẻ vị th&agrave;nh ni&ecirc;n c&oacute; &iacute;t tuyến mồ h&ocirc;i hơn người lớn v&agrave; sự điều h&ograve;a th&acirc;n nhiệt chưa ổn định, th&acirc;n nhiệt l&ecirc;n xuống thất thường. Ch&uacute; &yacute; kh&ocirc;ng n&ecirc;n mặc nhiều quần &aacute;o khi n&oacute;ng nực l&agrave; g&oacute;p phần l&agrave;m tăng th&acirc;n nhiệt của trẻ v&agrave; l&agrave;m&nbsp;<a href="http://phanphoi.com.vn/dung-nuoc-uong-lavie-de-nau-an-vi-mat-nuoc-sach.html/" style="color: rgb(179, 153, 100); border: 0px; margin: 0px; padding: 0px; transition: all 0.3s; -webkit-transition: all 0.3s; text-decoration: none;" target="_bank" title="Dùng nước uống Lavie để nấu ăn vì mất nước sạch"><strong>mất nước</strong></a>&nbsp;nhiều hơn. Uống nước kh&ocirc;ng cần qu&aacute; nhiều.</span></p>\r\n\r\n<p><span style="font-size:14px">Người cao tuổi thường bị bệnh tim, mạch v&agrave;nh, xơ vữa động mạch, tăng huyết &aacute;p&hellip;kh&ocirc;ng n&ecirc;n uống nhiều nước lạnh. Sức co b&oacute;p của tim ở những người n&agrave;y, vốn đ&atilde; kh&ocirc;ng được tốt, bỗng nhi&ecirc;n phải tăng g&aacute;nh nặng l&agrave;m việc đột ngột, c&oacute; thể g&acirc;y thiếu m&aacute;u, thiếu &ocirc;xy cơ tim, l&agrave;m tim bị đau thắt, loạn nhịp tim.</span></p>\r\n\r\n<p><span style="font-size:14px">B&agrave;i viết tr&ecirc;n đ&acirc;y t&ocirc;i đ&atilde; gi&uacute;p bạn c&oacute; được cho m&igrave;nh những c&aacute;ch&nbsp;<strong>uống nước thế n&agrave;o l&agrave; khoa học</strong>&nbsp;nhất gi&uacute;p bạn c&oacute; thể bảo vệ được sức khỏe của m&igrave;nh m&agrave; vẫn đảm bảo cung cấp được cho m&igrave;nh nguồn nước đầy đủ nhất v&agrave;o cơ thể, c&ugrave;ng với đ&oacute; l&agrave; những điều bạn n&ecirc;n tranh khi uống nước. Qua đ&oacute; t&ocirc;i cũng mong muốn bạn h&atilde;y lu&ocirc;n sử dụng cho m&igrave;nh nguồn&nbsp;<a href="http://phanphoi.com.vn/nuoc-tinh-khiet/" style="color: rgb(179, 153, 100); border: 0px; margin: 0px; padding: 0px; transition: all 0.3s; -webkit-transition: all 0.3s; text-decoration: none;" target="_blank" title="Nước tinh khiết">nước tinh khiết</a>&nbsp;nhất để đảm bảo cho m&igrave;nh một sức khỏe tốt nhất.</span></p>\r\n', 'admin', '2015-07-16 14:13:38', 1, 'drinking-water-danger.jpg'),
(3, 'Hướng dẫn sử dụng máy lọc nước tinh khiết RO', '<h2><span style="font-size:14px"><strong>C&aacute;c sự cố v&agrave; biện ph&aacute;p khắc phục:</strong></span></h2>\r\n\r\n<table border="1" cellpadding="0" cellspacing="0" style="color:rgb(0, 0, 0); font-family:arial; font-size:12px; line-height:normal; vertical-align:top; width:595px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:top"><span style="font-size:14px"><strong>Sự cố</strong></span></td>\r\n			<td style="text-align:center; vertical-align:top"><span style="font-size:14px"><strong>Hậu quả</strong></span></td>\r\n			<td style="text-align:center; vertical-align:top"><span style="font-size:14px"><strong>Biện ph&aacute;p khắc phục</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">1. Nước c&oacute; m&agrave;u đục</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Tạo ra kh&iacute; trong bộ lọc</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Sử dụng li&ecirc;n tục trong 1 tuần&nbsp; đầu . Hiện tượng n&agrave;y dần dần biến mất</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">2.C&oacute; tiếng k&ecirc;u từ v&ograve;i nước</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Hạn chế d&ograve;ng nước tho&aacute;t ra</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Khắc phục sự tắc nghẽn do cặn c&ograve;n mắc lại tại l&otilde;i lọc</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">3. Lượng nước trong b&igrave;nh &aacute;p thấp</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">&aacute;p suất trong b&igrave;nh &aacute;p thấp</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Tăng &aacute;p suất l&ecirc;n từ 5 &reg;6 PSI</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">4. Khả năng lọc chậm</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">&aacute;p suất nước thấp l&agrave;m chậm qu&aacute; tr&igrave;nh lọc trước đ&oacute;</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Kiểm tra lại hệt thống ống nước v&agrave; sửa lại nếu cần thiết</span>\r\n			<p><span style="font-size:14px">- Thay thế l&otilde;i lọc</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">5. Nước c&oacute; m&ugrave;i lạ</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">M&agrave;ng lọc than hoạt t&iacute;nh bị tắc</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Thay m&agrave;ng lọc than hoạt t&iacute;nh kh&aacute;c</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">6.Nước thải chảy qu&aacute; it</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Bị b&aacute;m bẩn</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Tốy bẩn</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">7.Hệ thống lọc bị r&ograve; nước</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Thiết bị kh&ocirc;ng k&iacute;n chặt</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Li&ecirc;n hệ tới nh&agrave; cung cấp</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style="font-size:14px"><strong>Chức năng c&aacute;c chi tiết ch&iacute;nh:</strong></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style="font-size:14px">T&iacute;nh năng ưu việt duy nhất chỉ c&oacute; tại sp kangaroo</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style="font-size:14px">Bảng tự động kiểm so&aacute;t hoạt động từ m&aacute;y R/O</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style="font-size:14px">Tự động rửa m&agrave;ng R/O trước khi lọc nước&nbsp;</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style="font-size:14px">Tự động b&aacute;o khi thiếu nước th&ocirc;</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style="font-size:14px">Tự ngừng hoạt động khi nước đầy b&igrave;nh ( Bạn sẽ kh&ocirc;ng bị l&atilde;ng ph&iacute; tiền điện )</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style="font-size:14px">Từ động xả nước thải</span></p>\r\n	</li>\r\n</ul>\r\n\r\n<h2><span style="font-size:14px"><strong>Nguy&ecirc;n l&yacute; hoạt động của phần điện:</strong></span></h2>\r\n\r\n<p><span style="font-size:14px"><strong>Vận h&agrave;nh ho&agrave;n to&agrave;n tự động</strong></span></p>\r\n\r\n<table border="1" cellpadding="0" cellspacing="0" style="color:rgb(0, 0, 0); font-family:arial; font-size:12px; line-height:normal; vertical-align:top">\r\n	<tbody>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Biến thế</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">AC 110/220,ra DC 24V cấp điện cho bơm R/O v&agrave; c&aacute;c van tự xả</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">C&ocirc;ng tắc hạ &aacute;p</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Tự ngắt nguồn khi nước dừng</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Bơm cao &aacute;p</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Tạo &aacute;p suất 100 PSI,đủ &aacute;p lực đẩy nước qua m&agrave;ng 0.0001 Micron</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">C&ocirc;ng tắc bơm cao &aacute;p</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Khi b&igrave;nh &aacute;p suất đầy th&igrave; m&aacute;y tự ngắt</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">B&igrave;nh &aacute;p chứa nước</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Dự trữ nước lọc tới 12.5lil</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><span style="font-size:14px"><strong>Cấu tạo v&agrave; chức năng của từng cột lọc:</strong></span></h2>\r\n\r\n<p><span style="font-size:14px"><strong>Cột lọc 1:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">Nguy&ecirc;n liệu: sợi PP,khe hở 5 micron</span></li>\r\n	<li><span style="font-size:14px">Thời gian thay thế : 6-8 th&aacute;ng/lần&oacute; 18.000 lit</span></li>\r\n	<li><span style="font-size:14px">Chức năng: Ngăn chặn bẩn b&ugrave;n đất rỉ s&eacute;t c&oacute; k&iacute;ch thước &gt;= 5 micron</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>Cột lọc 2:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">Nguy&ecirc;n liệu: than hoạt t&iacute;nh dạng sốp&nbsp;</span></li>\r\n	<li><span style="font-size:14px">Thời gian thay thế: 1năm /lần c&oacute; 27.000lit</span></li>\r\n	<li><span style="font-size:14px">Chức năng: khả năng hấp thụ cao m&ugrave;i vị,chất hữư cơ, thuốc trừ s&acirc;u, chất diệt c&ocirc;n tr&ugrave;ng, kim loại nặng clo dư trong nước.</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>Cột lọc 3:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">Nguy&ecirc;n liệu: Than hoạt t&iacute;nh thể rắn,khe hở 5 micro.</span></li>\r\n	<li><span style="font-size:14px">Thời gian thay thế: 1 năm/lần c&oacute; 36.000 lit.</span></li>\r\n	<li><span style="font-size:14px">Chức năng: Hấp thụ m&ugrave;i vị chất hữu xơ,thuốc trừ s&acirc;u,chất diệt c&ocirc;n tr&ugrave;ng, kim loại nặng clo dư trong nước. Ngo&agrave;i ra c&ograve;n c&oacute; chức năng ngan chạn b&ugrave;n đất rỉ s&eacute;t c&oacute; kich thước &gt;=5 micron.</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>Cột lọc 4:&nbsp;</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">Nguy&ecirc;n liệu: M&agrave;ng mỏng R/O sử dụng chất liệu đặc biệt tạo khe hở 0.0001 micron</span></li>\r\n	<li><span style="font-size:14px">Thời gian thay thế: 2-3 năm/lần &gt;=90.000 lit.</span></li>\r\n	<li><span style="font-size:14px">Chức năng: Loại bỏ ho&agrave;n to&agrave;n chất rắn, kh&iacute; h&ograve;a tan trong nước, c&aacute;c ion kim laọi nặng vi sinh vật vi khuẩn sai&ecirc;u vi khuẩn c&aacute;c chất hữu cơ l&agrave;m cho nước trở n&ecirc;n ho&agrave;n toan tinh khiết nhưng kh&ocirc;ng l&agrave;m thay đổi r&iacute;nh l&yacute; h&oacute;a của nước.</span></li>\r\n	<li><span style="font-size:14px"><strong>Ghi ch&uacute;:</strong>&nbsp;Đ&acirc;y l&agrave; m&agrave;ng lọc quan trọng nhất của to&agrave;n bộ hệ thống ,m&agrave;ng lọc sản xuất tại Mỹ nơi đ&aacute;p ứng đủ ti&ecirc;u chuẩn kỹ thuật.</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>Cột lọc 5:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">Nguy&ecirc;n liệu: Than hoạt t&iacute;nh khe hở 1 micron&nbsp;</span></li>\r\n	<li><span style="font-size:14px">Thời gian thay thế: 1 năm/lần. &gt;=72.000 lit.</span></li>\r\n	<li><span style="font-size:14px">Chức năng: C&acirc;n bằng độ PH loại bỏ ho&agrave;n to&agrave;n mu&igrave; vị trong nước.</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>Lưu &yacute;:</strong></span></p>\r\n\r\n<table border="1" cellpadding="5" cellspacing="0" style="color:rgb(0, 0, 0); font-family:arial; font-size:12px; line-height:normal; vertical-align:top; width:595px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">L&otilde;i lọc số</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">1</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">2</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">3</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">4</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">5</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">C&ocirc;ng suất lọc của mỗi l&otilde;i/l&iacute;t</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">25.000 lit</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">35.000 l&iacute;t</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">46.000 l&iacute;t</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">115.000 l&iacute;t</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">85.000 l&iacute;t</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><span style="font-size:14px"><strong>M&agrave;u sắc nước sau khi điện ph&acirc;n v&agrave; bộ phận cơ thể</strong>&nbsp;<strong>bị thương tổn:</strong></span></h2>\r\n\r\n<table border="1" cellpadding="0" cellspacing="0" style="color:rgb(0, 0, 0); font-family:arial; font-size:12px; line-height:normal; vertical-align:top; width:595px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">M&agrave;u sắc</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Khả năng chất nhiễm bẩn</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">Bộ phận khẳ năng thương tổn</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">M&agrave;u xanh</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">- Axit sunfat đồng(1)<br />\r\n			- Ox&iacute;t đồng(rỉ đồng)<br />\r\n			-&nbsp;Những chất độc thuộc clo(2)</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">-&nbsp; thận trung khu thần kinh<br />\r\n			-&nbsp;chất g&acirc;y ung thư</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">M&agrave;u đen</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">- Hợp chất kim loại nặng thủy ng&acirc;n(Hg),Ch&igrave;(Pb)<br />\r\n			-&nbsp;Chất oxi h&oacute;a canxi,Magie.<br />\r\n			-&nbsp;Kẽm (Zn) oxit kẽm.</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">- Trung khu TK,gan thận<br />\r\n			-&nbsp;Loại CaCO3 kết hợp với MgCO3 g&acirc;y n&ecirc;n hiện tượng v&ocirc;i cột sống</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">M&agrave;u trắng</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nh&ocirc;m(Al) oxit nh&ocirc;m</span>\r\n			<p><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; As(Asenic)AS2O2</span></p>\r\n\r\n			<p><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Keo</span></p>\r\n\r\n			<p><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Sợi đ&aacute; thạch ni&ecirc;n</span></p>\r\n			</td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thần kinh gan</span>\r\n			<p><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thần kinh,Chất g&acirc;y ung thư.</span></p>\r\n\r\n			<p><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ung thư</span></p>\r\n\r\n			<p><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Vi khu&acirc;n độc tố g&acirc;y bện ung thư</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">M&agrave;u lam</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Axit sunfua nh&ocirc;m(Al2 SO3)</span>\r\n			<p><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Chất hữu cơ P(photpho)(3)</span></p>\r\n			</td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hệ thống thần kinh</span>\r\n			<p><span style="font-size:14px">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gan ,thận,Thần kinh</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top"><span style="font-size:14px">M&agrave;u cam</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">-Chất oxit sắt Fe2 O3</span></td>\r\n			<td style="vertical-align:top"><span style="font-size:14px">- N&ocirc;n mửa mất nước,bệnh về tiết niệu,mất khẳ năng điều chỉnh</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style="font-size:14px">Chất kim loại nặng t&iacute;ch lũy trong cơ thể, thời gian l&acirc;u sẽ g&acirc;y n&ecirc;n tai biến thận thần kinh v&agrave; gan.</span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">(1): C&oacute; nơi đổ sunfat đồng v&agrave;o chỗ đưng nước để diệt vi khuẩn.</span></li>\r\n	<li><span style="font-size:14px">(2): Clo do c&aacute;c c&ocirc;ng ty cung cấp nước d&ugrave;ng diệt khuẩn.</span></li>\r\n	<li><span style="font-size:14px">(3): Thuốc trừ s&acirc;u,trừ cỏ s&aacute;t tr&ugrave;ng n&oacute;i chung đều c&oacute; P(photpho).</span></li>\r\n</ul>\r\n\r\n<h2><span style="font-size:14px"><strong>C&aacute;c sự cố v&agrave; biện ph&aacute;p khắc phục:</strong></span></h2>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">M&aacute;y bơm l&agrave;m việc kh&ocirc;ng li&ecirc;n tục c&oacute; tiếng k&ecirc;u ngắt qu&atilde;ng &aacute;p suất nước nguồn v&agrave;o kh&ocirc;ng đủ: thay l&otilde;i lọc số 1 hoặc đặt bơm phụ hỗ trợ nước nguồn v&agrave;o. Nếu cần đấu tắc điện qua c&ocirc;ng tắc hạ &aacute;p.</span></li>\r\n	<li><span style="font-size:14px">M&aacute;y bơm kh&ocirc;ng chạy: C&oacute; dị tật ở van v&agrave;o, qu&ecirc;n kh&ocirc;ng mở van.</span></li>\r\n	<li><span style="font-size:14px">Mở van v&agrave;o: Mở van hoặc thay van kh&aacute;c.</span></li>\r\n	<li><span style="font-size:14px">Động cơ chạy nhưng kh&ocirc;ng đủ &aacute;p suất: Điều chỉnh n&uacute;t &aacute;p suất tăng giảm v&agrave; đẻ tăng v&agrave; ngược lại.</span></li>\r\n	<li><span style="font-size:14px">Đầu van v&agrave;o bơm c&oacute; dị tật hoặc đ&oacute;ng căn than: L&agrave;m sạch hoặc thay van kh&aacute;c</span></li>\r\n	<li><span style="font-size:14px">Nước ch&aacute;y qu&aacute; nhiều: Cổng kiểm tra tự xả bị b&aacute;m bẩn &ndash; pittong b&oacute; =&gt; tẩy bẩn v&agrave; l&agrave;m sạch pittong &aacute;p suất lọc b&igrave;nh thường nhưng kh&ocirc;ng c&oacute; nước lọc v&agrave; nước thải Lớp than hoạt t&iacute;nh trước bị b&oacute;.</span></li>\r\n	<li><span style="font-size:14px">Nước lọc ra qu&aacute; nhiều &gt; 40PPM: Thay m&agrave;ng R/O</span></li>\r\n	<li><span style="font-size:14px">Sau khi nước đầy b&igrave;nh &aacute;p m&agrave; m&aacute;y bơm kh&ocirc;ng dừng hoạt động: Lỗ m&agrave;ng R/O qu&aacute; rộng l&agrave;m giảm hiệu quả lọc =&gt; thay</span></li>\r\n	<li><span style="font-size:14px">B&igrave;nh &aacute;p suất nước đầy m&agrave; nước ra &iacute;t - kh&ocirc;ng chảy: Điều chỉnh lại van ngắt b&igrave;nh &aacute;p hoặc thay van kh&aacute;c</span></li>\r\n	<li><span style="font-size:14px">Đầu bơm R/O r&ograve; nước: Thay gioăng chống thấm.</span></li>\r\n</ul>\r\n', 'admin', '2015-07-16 15:36:46', 1, ''),
(4, 'Hướng dẫn lắp đặt máy lọc nước RO Kangaroo', '<h3 style="text-align:justify"><span style="font-size:14px">Để lắp đặt được m&aacute;y lọc nước RO Kangaroo ta cần chuẩn bị như sau:</span></h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><strong>1. Vị tr&iacute; lắp đặt:</strong></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><strong>&nbsp; -&nbsp;</strong>Chọn vị tr&iacute; lắp&nbsp;<em>đặt&nbsp;<a href="http://maylocnuocgiadinh.com.vn/may-loc/may-loc-nuoc-kangaroo.htm" style="color: rgb(51, 51, 51); outline: none; text-decoration: none;">m&aacute;y lọc nước Kangaroo&nbsp;</a>gần nguồn nước</em>,&nbsp;<em>nguồn điện</em>&nbsp;thuận tiện cho việc loại bỏ nước thải. Đặc biệt, bạn n&ecirc;n chọn vị tr&iacute; lắp đặt kh&ocirc;ng bị &aacute;nh nắng chiếu rọi, nơi c&oacute; nhiệt độ cao, hoặc dễ đ&oacute;ng băng.</span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">&nbsp; - Vị tr&iacute; lắp đặt phải thuận lợi cho việc bảo tr&igrave;, bảo dưỡng, thay thế l&otilde;i lọc.</span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">&nbsp; - Chọn nơi m&agrave; ống nước được đặt cố định, hoạt động bền vững m&agrave; kh&ocirc;ng bẻ cong, rung giật hoặc bị vật kh&aacute;c ngoắc v&agrave;o.</span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><strong>2. Những lưu &yacute; để xử dụng m&aacute;y lọc nước RO Kangaroo an to&agrave;n</strong></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">&nbsp; - Nước nguồn cung cấp cho<a href="http://maylocnuocgiadinh.com.vn/" style="color: rgb(51, 51, 51); outline: none; text-decoration: none;"><strong>&nbsp;m&aacute;y lọc nước&nbsp;</strong></a>&nbsp;phải c&oacute; khả năng uống được, kh&ocirc;ng kết nối nguồn nước n&oacute;ng với m&aacute;y lọc nước Kangaroo.</span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">&nbsp; - Tất cả c&aacute;c vị tr&iacute; lắp gh&eacute;p đều phải được quấn băng tan trước khi lắp gh&eacute;p ( để lắp gh&eacute;p xoay chi tiết cần lắp theo chiều kim đồng hồ. Sau khi nắp xong kh&ocirc;ng lay, lắc, xoay ngược lại để tr&aacute;nh r&ograve; rỉ nước.</span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">&nbsp; - Cuối đường d&acirc;y nước phải được cắt v&aacute;t một g&oacute;c 45o để tr&aacute;nh hiện tượng tắc ống.</span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">&nbsp; - Sau khi nắp xong, bạn n&ecirc;n xả nước từ 1 - 2 ph&uacute;t để l&agrave;m sạch c&aacute;c đường ống nước v&agrave; than hoạt t&iacute;nh.</span></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<h3 style="text-align:justify"><span style="font-size:14px">C&aacute;c bước nắp đặt m&aacute;y lọc nước RO Kangaroo</span></h3>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="Sơ đồ lắp đặt máy lọc nước Kangaroo" src="http://maylocnuocgiadinh.com.vn/upload/FCK/so-do-lap-dat-may-loc-nuoc-kangaroo(2).jpg" style="border:0px; height:392px; width:677px" /></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><u>Trong đ&oacute;:</u></span></p>\r\n\r\n<table border="1" cellpadding="1" cellspacing="1" style="background-color:rgb(254, 254, 254); font-family:arial,helvetica,sans-serif; font-size:12px; height:177px; line-height:19px; text-align:justify; width:780px">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style="font-size:14px">V1</span></td>\r\n			<td><span style="font-size:14px">Van cấp nước</span></td>\r\n			<td><span style="font-size:14px">1</span></td>\r\n			<td><span style="font-size:14px">Cột lọc số 1</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-size:14px">V2</span></td>\r\n			<td><span style="font-size:14px">Van &aacute;p thấp</span></td>\r\n			<td><span style="font-size:14px">2</span></td>\r\n			<td><span style="font-size:14px">Cột lọc số 2</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-size:14px">V3A</span></td>\r\n			<td><span style="font-size:14px">Van Flow</span></td>\r\n			<td><span style="font-size:14px">3</span></td>\r\n			<td><span style="font-size:14px">Cột lọc số 3</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-size:14px">V3B</span></td>\r\n			<td><span style="font-size:14px">Van kh&oacute;a</span></td>\r\n			<td><span style="font-size:14px">4</span></td>\r\n			<td><span style="font-size:14px">Cột lọc số 4</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-size:14px">V4</span></td>\r\n			<td><span style="font-size:14px">Van &aacute;p cao</span></td>\r\n			<td><span style="font-size:14px">5</span></td>\r\n			<td><span style="font-size:14px">Cột lọc số 5</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-size:14px">V5</span></td>\r\n			<td><span style="font-size:14px">Van điều tiết</span></td>\r\n			<td><span style="font-size:14px">6</span></td>\r\n			<td><span style="font-size:14px">Cột lọc số 6</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-size:14px">V6</span></td>\r\n			<td><span style="font-size:14px">Van 1 chiều ( m&agrave;u xanh&nbsp;)</span></td>\r\n			<td><span style="font-size:14px">7</span></td>\r\n			<td><span style="font-size:14px">Cột lọc số 7</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-size:14px">V7</span></td>\r\n			<td><span style="font-size:14px">Van nạp kh&iacute; ( kh&ocirc;ng tự &yacute; điều chỉnh )</span></td>\r\n			<td><span style="font-size:14px">-&gt;</span></td>\r\n			<td><span style="font-size:14px">Chỉ hướng đi của nước trong m&aacute;y</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-size:14px">V8</span></td>\r\n			<td><span style="font-size:14px">Van b&igrave;nh &aacute;p ( chỉ đ&oacute;ng khi sửa chữa )</span></td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="Hướng dẫn lắp dặt 1" src="http://maylocnuocgiadinh.com.vn/upload/FCK/Huong-dan-lap-dat-1.jpg" style="border:0px; height:535px; width:597px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="lắp đặt máy lọc nước 2" src="http://maylocnuocgiadinh.com.vn/upload/FCK/thao-vo-may-loc-nuoc-2.jpg" style="border:0px; height:290px; width:600px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="hướng dẫn lắp đặt 3" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-3.jpg" style="border:0px; height:291px; width:632px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px">&nbsp;<img alt="lắp đặt máy lọc nước 4" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-4.jpg" style="border:0px; height:293px; width:650px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px">&nbsp;<img alt="lắp đặt máy lọc nước 5" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-5.jpg" style="border:0px; height:295px; width:610px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="lắp đặt máy lọc nước 7" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-6.jpg" style="border:0px; height:313px; width:612px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="lắp đặt máy lọc nước 7" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-7.jpg" style="border:0px; height:550px; width:616px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="lắp đặt máy lọc nước 8" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-08.jpg" style="border:0px; height:305px; width:628px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="lắp đặt máy lọc nước 9" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-09.jpg" style="border:0px; height:283px; width:600px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="lắp đặt máy lọc nước 10" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-10.jpg" style="border:0px; height:297px; width:633px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="lắp đặt máy lọc nước 11" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-11.jpg" style="border:0px; height:283px; width:619px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="lắp đặt máy lọc nước 12" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-12.jpg" style="border:0px; height:281px; width:623px" /></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><img alt="lắp đặt máy lọc nước 13" src="http://maylocnuocgiadinh.com.vn/upload/FCK/lap-dat-may-loc-nuoc-13.jpg" style="border:0px; height:290px; width:602px" /></span></p>\r\n\r\n<p><em><span style="font-size:14px">Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng !</span></em></p>\r\n\r\n<p><span style="font-size:14px">Nếu c&aacute;c bạn c&oacute; thắc mắc về c&aacute;ch lắp đặt m&aacute;y lọc nước Kangaroo xin vui l&ograve;ng li&ecirc;n hệ <span style="color:#0000FF"><strong>Hotline 0904 501 820</strong></span> để được hướng dẫn chi tiết. Cảm ơn c&aacute;c bạn đ&atilde; ủng hộ sản phẩm của ch&uacute;ng t&ocirc;i.</span></p>\r\n', 'admin', '2015-07-16 15:38:40', 1, ''),
(5, 'Hướng dẫn lắp đặt máy lọc nước RO Karofi', '<p>&nbsp;</p>\r\n\r\n<p style="text-align: center;"><strong>Quy tr&igrave;nh lắp đặt</strong></p>\r\n\r\n<p><span style="font-size:14px"><a href="http://showroomkarofi.com.vn/" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(221, 0, 0);"><strong>M&aacute;y lọc nước Karofi</strong></a>&nbsp;c&oacute; b&igrave;nh &aacute;p bằng nhựa hay bằng th&eacute;p th&igrave; quy tr&igrave;nh lắp đặt tương tự nhau.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 1</strong>: D&ugrave;ng kh&oacute;a c&uacute;t inox đầu v&agrave;o chưa t&aacute;ch nguồn nước của gia đ&igrave;nh để đưa v&agrave;o hệ thống m&aacute;y Karofi</span></p>\r\n\r\n<p><span style="font-size:14px">Lưu &yacute;: giữ c&uacute;t đầu v&agrave;o ở trạng th&aacute;i kh&oacute;a, chưa cho nước v&agrave;o hệ thống lọc.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 2</strong>: B&oacute;c vỏ nilon l&otilde;i số 1,2,3 cho v&agrave;o cốc lọc tương ứng. Sau đ&oacute; vặn chặt cốc lọc số 2 v&agrave; số 3, cốc 1 vặn vừa phải để lỏng hơi.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 3</strong>: Nối d&acirc;y RO to với c&uacute;t đầu v&agrave;o của cốc số 1. Khi lắp đặt đảm bảo c&uacute;t ốc phải được quấn băng tan v&agrave; xiết hết ren nhắm tối ưu lực li&ecirc;n kết giữa d&acirc;y RO v&agrave; c&uacute;t ốc.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 4</strong>: Tại c&uacute;t đầu ra của cốc số 3, th&aacute;o d&acirc;y RO nối từ vị tr&iacute; n&agrave;y với đầu v&agrave;o của m&agrave;ng RO, sau đ&oacute; lấy đoạn d&acirc;y RO nhỏ (đoạn b&ecirc;n ngo&agrave;i) nối trực tiếp từ c&uacute;t đầu ra với cốc số 3 đến vị tr&iacute; xả nước để chuẩn bị qu&aacute; tr&igrave;nh sục rửa l&otilde;i số 1,2,3.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 5</strong>: cắm ph&iacute;ch điện của m&aacute;y RO v&agrave;o nguồn điện, đồng thời mở van nước của c&uacute;t inox đầu v&agrave;o để bắt đầu sục rửa sạch c&aacute;c chất bảo quản của l&otilde;i số 1,2,3. Qu&aacute; tr&igrave;nh n&agrave;y k&eacute;o d&agrave;i 20 ph&uacute;t.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 6</strong>: Trong qu&aacute; tr&igrave;nh sục rửa thấy nước từ cốc số 1 tr&agrave;n ra ngo&agrave;i th&igrave; tiến h&agrave;nh xiết chặt lại cốc số 1.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 7</strong>: Sau thời gian khoảng 20 ph&uacute;t tiến h&agrave;ng sục rửa l&otilde;i số 1,2,3 ta r&uacute;t ph&iacute;ch điện ra, đồng thời nối lại d&acirc;y RO từ đầu v&agrave;o của vỏ m&agrave;ng RO đến đầu v&agrave;o của cốc lọc số 3 (như trạng th&aacute;i ban đầu)</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 8</strong>: B&oacute;c t&uacute;i nilon m&agrave;ng RO (Lưu &yacute;: khi b&oacute;c l&ecirc;n đeo găng tay y tế để đảm bảo an to&agrave;n vệ sinh đối với nguồn nước tinh khiết được lọc qua m&agrave;ng).</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 9</strong>: Thấm m&agrave;ng RO bằng nước tinh khiết (khuyến nghị n&ecirc;n d&ugrave;ng Aquafina) để cấu tr&uacute;c m&agrave;ng TO gi&atilde;n nở đều nhằm đạt được tối đa trong qu&aacute; tr&igrave;nh lọc.</span></p>\r\n\r\n<p><em><span style="font-size:14px">Lưu &yacute;: khi lắp r&aacute;p phải đeo găng tay y tế ở bước n&agrave;y để đảm bảo an to&agrave;n vệ sinh cho nguồn nước lọc sau m&agrave;ng RO.</span></em></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 10</strong>: Đưa v&agrave; ấn chặt m&agrave;ng RO v&agrave;o ph&iacute;a trong vỏ m&agrave;ng (khi lắp r&aacute;p ta cần đeo găng tay y tế khi thực hiện bước n&agrave;y).</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 11</strong>: Đậy v&agrave; vặn chặt nắp vỏ m&agrave;ng RO.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 12</strong>: Nối d&acirc;y nhỏ của m&agrave;ng RO từ vị tr&iacute; đầu chờ của van Flow, nối với d&ograve;ng nước xả thải của gia đ&igrave;nh.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 13</strong>: Tại c&uacute;t đầu ra của m&agrave;ng RO (c&uacute;t kết nối với d&acirc;y xanh) th&aacute;o rời d&acirc;y m&agrave;u xanh, sau đ&oacute; nối đoạn d&acirc;y RO nhỏ (d&acirc;y b&ecirc;n ngo&agrave;i) ra nơi xả nước của gia đ&igrave;nh.</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>Bước 14</strong>: Căm ph&iacute;ch điện của m&aacute;y v&agrave;o nguồn điện để m&aacute;y bắt đầu qu&aacute; tr&igrave;nh sục rửa chất bảo quản trong khoảng thời gian 20 ph&uacute;t.</span></p>\r\n\r\n<h2><span style="font-size:14px"><strong>Sơ đồ lắp đặt m&aacute;y lọc nước RO Karofi</strong></span></h2>\r\n\r\n<p>Khi lắp đặt m&aacute;y lọc nước RO karofi bạn cần lắm vững được sơ đồ, nguy&ecirc;n l&yacute; hoạt động của m&aacute;y. Dưới đ&acirc;y l&agrave; sơ đồ lắp đặt m&aacute;y lọc nước RO karofi.</p>\r\n\r\n<p><img alt="" src="/upload/ckfinder/images/News/07-16-2015/so-do-lap-may-loc-nuoc-karofi.png" style="height:390px; width:800px" /></p>\r\n', 'admin', '2015-07-16 18:18:31', 1, 'child-vater.jpg');
INSERT INTO `tbl_news` (`id`, `name`, `content`, `author`, `date`, `active`, `image`) VALUES
(6, 'Các chỉ tiêu cần biết trong nước sạch', '<p>X&eacute;t nghiệm nước l&agrave; c&ocirc;ng việc cần l&agrave;m để t&igrave;m ra những vấn đề của nguồn nước. Dựa tr&ecirc;n kết quả x&eacute;t nghiệm ta c&oacute; thể dễ d&agrave;ng chọn lựa c&ocirc;ng nghệ v&agrave; thiết bị để xử l&yacute; nước.</p>\r\n\r\n<p>Sơ lược c&aacute;c chỉ số trong&nbsp;bảng x&eacute;t nghiệm như sau:</p>\r\n\r\n<p><strong>1. M&ugrave;i vị</strong></p>\r\n\r\n<p>- Nước giếng ngầm: m&ugrave;i trứng thối l&agrave; do c&oacute; kh&iacute; H2S, kết quả của qu&aacute; tr&igrave;nh ph&acirc;n hủy c&aacute;c chất hữu cơ trong l&ograve;ng đất v&agrave; h&ograve;a tan v&agrave;o mạch nước ngầm. M&ugrave;i tanh của sắt v&agrave; mangan.</p>\r\n\r\n<p>- Nước mặt (s&ocirc;ng, suối, ao hồ): m&ugrave;i tanh của tảo l&agrave; do sự xuất hiện của c&aacute;c loại tảo v&agrave; vi sinh vật. Trong trường hợp n&agrave;y nước thường c&oacute; m&agrave;u xanh.</p>\r\n\r\n<p>- Nước m&aacute;y: m&ugrave;i h&oacute;a chất khử tr&ugrave;ng (clo) c&ograve;n dư lại trong nước.</p>\r\n\r\n<p>M&ugrave;i vị kh&aacute;c lạ sẽ g&acirc;y cảm gi&aacute;c kh&oacute; chịu khi d&ugrave;ng nước. Tuỳ theo loại m&ugrave;i vị m&agrave; c&oacute; c&aacute;ch xử l&yacute; ph&ugrave; hợp như d&ugrave;ng h&oacute;a chất diệt tảo trong ao hồ, keo tụ lắng lọc, hấp phụ bằng than hoạt t&iacute;nh,&hellip;</p>\r\n\r\n<p><strong>2. M&agrave;u</strong></p>\r\n\r\n<p><strong>-&nbsp;</strong>M&agrave;u v&agrave;ng của hợp chất sắt v&agrave; mangan.</p>\r\n\r\n<p>- M&agrave;u xanh của tảo, hợp chất hữu cơ.</p>\r\n\r\n<p>Nước c&oacute; độ m&agrave;u cao thường g&acirc;y kh&oacute; chịu về mặt cảm quan. Với c&aacute;c quy tr&igrave;nh xử l&yacute; như sục kh&iacute; oz&ocirc;n, clo h&oacute;a sơ bộ, keo tụ, lắng lọc c&oacute; thể l&agrave;m giảm độ m&agrave;u của nước. Cần lưu &yacute;, khi nguồn nước c&oacute; m&agrave;u do hợp chất hữu cơ, việc sử dụng Clo c&oacute; thể tạo ra chất mới l&agrave; trihalomethane c&oacute; khả năng g&acirc;y ung thư.</p>\r\n\r\n<p><strong>3. pH</strong></p>\r\n\r\n<p>Nguồn nước c&oacute; pH &gt; 7 thường chứa nhiều ion nh&oacute;m carbonate v&agrave; bicarbonate (do chảy qua nhiều tầng đất đ&aacute;). Nguồn nước c&oacute; pH &lt; 7 thường chứa nhiều ion gốc axit.</p>\r\n\r\n<p>Bằng chứng dễ thấy nhất li&ecirc;n quan giữa độ pH v&agrave; sức khỏe của người sử dụng l&agrave; n&oacute; l&agrave;m hỏng men răng pH của nước c&oacute; li&ecirc;n quan đến t&iacute;nh ăn m&ograve;n thiết bị, đường ống dẫn nước v&agrave; dụng cụ chứa nước. Đặc biệt, trong m&ocirc;i trường pH thấp, khả năng khử tr&ugrave;ng của Clo sẽ mạnh hơn. Tuy nhi&ecirc;n, khi pH &gt; 8,5 nếu trong nước c&oacute; hợp chất hữu cơ th&igrave; việc khử tr&ugrave;ng bằng Clo dễ tạo th&agrave;nh hợp chất trihalomethane g&acirc;y ung thư.</p>\r\n\r\n<p>Theo ti&ecirc;u chuẩn, pH của nước sử dụng cho sinh hoạt l&agrave; 6,0 &ndash; 8,5 v&agrave; của nước uống l&agrave; 6,5 &ndash; 8,5.</p>\r\n\r\n<p><strong>4. Độ đục</strong></p>\r\n\r\n<p>Độ đục l&agrave; đại lượng đo h&agrave;m lượng chất lơ lửng trong nước, thường do sự hiện diện của chất keo, s&eacute;t, tảo v&agrave; vi sinh vật.</p>\r\n\r\n<p>Nước đục g&acirc;y cảm gi&aacute;c kh&oacute; chịu cho người d&ugrave;ng v&agrave; c&oacute; khả năng nhiễm vi sinh. Ti&ecirc;u chuẩn nước sạch quy định độ đục nhỏ hơn 5NTU, nhưng giới hạn tối đa của nước uống chỉ l&agrave; 2 NTU. C&aacute;c quy tr&igrave;nh xử l&yacute; như keo tụ, lắng, lọc g&oacute;p phần l&agrave;m giảm độ đục của nước.</p>\r\n\r\n<p><strong>5. Độ kiềm</strong></p>\r\n\r\n<p>Độ kiềm của nước l&agrave; do c&aacute;c ion bicarbonate, carbonate v&agrave; hydroxide tạo n&ecirc;n. Trong th&agrave;nh phần h&oacute;a học của nước, độ kiềm c&oacute; li&ecirc;n quan đến c&aacute;c chỉ ti&ecirc;u kh&aacute;c như pH, độ cứng v&agrave; tổng h&agrave;m lượng kho&aacute;ng. Việc x&aacute;c định độ kiềm của nước gi&uacute;p cho việc định lượng h&oacute;a chất trong qu&aacute; tr&igrave;nh keo tụ, l&agrave;m mềm nước cũng như xử l&yacute; chống ăn m&ograve;n.</p>\r\n\r\n<p>Hiện nay, kh&ocirc;ng c&oacute; bằng chứng cụ thể n&agrave;o li&ecirc;n quan giữa độ kiềm v&agrave; sức khỏe của người sử dụng. Th&ocirc;ng thường, nước d&ugrave;ng cho ăn uống n&ecirc;n c&oacute; độ kiềm thấp hơn 100 mg/l.</p>\r\n\r\n<p><strong>6. Độ cứng</strong></p>\r\n\r\n<p>Độ cứng l&agrave; đại lượng đo tổng c&aacute;c cation đa h&oacute;a trị c&oacute; trong nước, nhiều nhất l&agrave; ion canxi v&agrave; magi&ecirc;. Nước mặt thường kh&ocirc;ng c&oacute; độ cứng cao như nước ngầm. T&ugrave;y theo độ cứng của nước người ta chia th&agrave;nh c&aacute;c loại sau:</p>\r\n\r\n<p>- Độ cứng từ 0 &ndash; 50mg/l -&gt; Nước mềm</p>\r\n\r\n<p>- Độ cứng từ 50 &ndash; 150mg/l -&gt; Nước hơi cứng</p>\r\n\r\n<p>- Độ cứng từ 150 &ndash; 300mg/l -&gt; Nước cứng</p>\r\n\r\n<p>- Độ cứng &gt; 300mg/l -&gt; Nước rất cứng</p>\r\n\r\n<p>Nước cứng thường cần nhiều x&agrave; ph&ograve;ng hơn để tạo bọt, hoặc g&acirc;y hiện tượng đ&oacute;ng cặn trắng trong thiết bị đun, ống dẫn nước n&oacute;ng, thiết bị giải nhiệt hay l&ograve; hơi. Ngược lại, nước cứng thường kh&ocirc;ng g&acirc;y hiện tượng ăn m&ograve;n đường ống v&agrave; thiết bị.Theo ti&ecirc;u chuẩn nước sạch, độ cứng được quy định nhỏ hơn 350 mg/l. Đối với nước ăn uống, độ cứng nhỏ hơn 300 mg/l. Tuy nhi&ecirc;n, khi độ cứng vượt qu&aacute; 50 mg/l, trong c&aacute;c thiết bị đun nấu đ&atilde; xuất hiện cặn trắng. Trong th&agrave;nh phần của độ cứng, canxi v&agrave; magi&ecirc; l&agrave; 2 yếu tố quan trọng thường được bổ sung cho cơ thể qua đường thức ăn. Tuy nhi&ecirc;n, những người c&oacute; nguy cơ mắc bệnh sỏi thận cần hạn chế việc hấp thụ canxi v&agrave; magi&ecirc; ở h&agrave;m lượng cao.</p>\r\n\r\n<p>C&oacute; thể khử độ cứng bằng phương ph&aacute;p trao đổi ion.</p>\r\n\r\n<p><strong>7. Tổng chất rắn h&ograve;a tan (TDS)</strong></p>\r\n\r\n<p>TDS l&agrave; đại lượng đo tổng chất rắn h&ograve;a tan c&oacute; trong nước, hay c&ograve;n gọi l&agrave; tổng chất kho&aacute;ng.</p>\r\n\r\n<p>Ti&ecirc;u chuẩn nước sạch quy định TDS nhỏ hơn 1.000 mg/l. Ti&ecirc;u chuẩn nước uống quy định TDS nhỏ hơn 500 mg/l.</p>\r\n\r\n<p><strong>8. Độ oxy h&oacute;a (Chất hữu cơ)</strong></p>\r\n\r\n<p>Độ oxy h&oacute;a được d&ugrave;ng để đ&aacute;nh gi&aacute; mức độ &ocirc; nhiễm của nguồn nước. C&oacute; 2 phương ph&aacute;p x&aacute;c định độ oxy h&oacute;a t&ugrave;y theo h&oacute;a chất sử dụng l&agrave; phương ph&aacute;p KMnO4 v&agrave; K2CrO7.</p>\r\n\r\n<p>Ti&ecirc;u chuẩn nước sạch quy định độ oxy h&oacute;a theo KMnO44) nhỏ hơn 2 mg/l. nhỏ hơn 4 mg/l. Ti&ecirc;u chuẩn nước uống quy định độ oxy h&oacute;a (theo KMnO</p>\r\n\r\n<p><strong>9. Nh&ocirc;m</strong></p>\r\n\r\n<p>Nh&ocirc;m l&agrave; th&agrave;nh phần ch&iacute;nh trong c&aacute;c loại đ&aacute; kho&aacute;ng, đất s&eacute;t. Nh&ocirc;m được d&ugrave;ng trong c&aacute;c ng&agrave;nh c&ocirc;ng nghiệp sản xuất chất b&aacute;n dẫn, thuốc nhuộm, sơn v&agrave; đặc biệt l&agrave; h&oacute;a chất keo tụ trong xử l&yacute; nước. Nước khai th&aacute;c từ v&ugrave;ng đất nhiễm ph&egrave;n thường c&oacute; độ pH thấp v&agrave; h&agrave;m lượng nh&ocirc;m cao.</p>\r\n\r\n<p>Nh&ocirc;m kh&ocirc;ng g&acirc;y rối loạn cơ chế trao đổi chất, tuy nhi&ecirc;n c&oacute; li&ecirc;n quan đến c&aacute;c bệnh Alzheimei v&agrave; gia tăng qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a. Ti&ecirc;u chuẩn nước uống quy định h&agrave;m lượng nh&ocirc;m nhỏ hơn 0,2 mg/l.</p>\r\n\r\n<p><strong>10. Sắt</strong></p>\r\n\r\n<p>Do ion sắt hai dễ bị oxy h&oacute;a th&agrave;nh hydroxyt sắt ba, tự kết tủa v&agrave; lắng n&ecirc;n sắt &iacute;t tồn tại trong nguồn nước mặt. Đối với nước ngầm, trong điều kiện thiếu kh&iacute;, sắt thường tồn tại ở dạng ion Fe2+ v&agrave; ho&agrave; tan trong nước. Khi được l&agrave;m tho&aacute;ng, sắt hai sẽ chuyển h&oacute;a th&agrave;nh sắt ba, xuất hiện kết tủa hydroxyt sắt ba c&oacute; m&agrave;u v&agrave;ng, dễ lắng. Trong trường hợp nguồn nước c&oacute; nhiều chất hữu cơ, sắt c&oacute; thể tồn tại ở dạng keo (phức hữu cơ) rất kh&oacute; xử l&yacute;. Ngo&agrave;i ra, nước c&oacute; độ pH thấp sẽ g&acirc;y hiện tượng ăn m&ograve;n đường ống v&agrave; dụng cụ chứa, l&agrave;m tăng h&agrave;m lượng sắt trong nước.</p>\r\n\r\n<p>Sắt kh&ocirc;ng g&acirc;y độc hại cho cơ thể. Khi h&agrave;m lượng sắt cao sẽ l&agrave;m cho nước c&oacute; vị tanh, m&agrave;u v&agrave;ng, độ đục v&agrave; độ m&agrave;u tăng n&ecirc;n kh&oacute; sử dụng. Ti&ecirc;u chuẩn nước uống v&agrave; nước sạch đều quy định h&agrave;m lượng sắt nhỏ hơn 0,5 mg/l.&nbsp;<br />\r\n<strong>11. Mangan</strong></p>\r\n\r\n<p>Mangan thường tồn tại trong nước c&ugrave;ng với sắt nhưng với h&agrave;m lượng &iacute;t hơn. Khi trong nước c&oacute; mangan thường tạo lớp cặn m&agrave;u đen đ&oacute;ng b&aacute;m v&agrave;o th&agrave;nh v&agrave; đ&aacute;y bồn chứa.</p>\r\n\r\n<p>Mangan c&oacute; độc t&iacute;nh rất thấp v&agrave; kh&ocirc;ng g&acirc;y ung thư. Ở h&agrave;m lượng cao hơn 0,15 mg/l c&oacute; thể tạo ra vị kh&oacute; chịu, l&agrave;m hoen ố quần &aacute;o. Ti&ecirc;u chuẩn nước uống v&agrave; nước sạch đều quy định h&agrave;m lượng mangan nhỏ hơn 0,5 mg/l.&nbsp;<br />\r\n<br />\r\n<strong>12. Asen (thạch t&iacute;n)</strong></p>\r\n\r\n<p>Do thấm qua nhiều tầng địa chất kh&aacute;c nhau, nước ngầm thường chứa asen nhiều hơn nước mặt. Ngo&agrave;i ra asen c&oacute; mặt trong nguồn nước khi bị nhiễm nước thải c&ocirc;ng nghiệp, thuốc trừ s&acirc;u.</p>\r\n\r\n<p>Khi bị nhiễm asen, c&oacute; khả năng g&acirc;y ung thư da v&agrave; phổi. Ti&ecirc;u chuẩn nước sạch quy định asen nhỏ hơn 0,05 mg/l. Ti&ecirc;u chuẩn nước uống quy định asen nhỏ hơn 0,01 mg/l.</p>\r\n\r\n<p><strong>13. Cadimi</strong></p>\r\n\r\n<p>Do thấm qua nhiều tầng địa chất kh&aacute;c nhau, nước ngầm thường chứa h&agrave;m lượng cadimi nhiều hơn nước mặt. Ngo&agrave;i ra Cadimi c&ograve;n thấy trong nguồn nước bị nhiễm nước thải c&ocirc;ng nghiệp khai th&aacute;c mỏ, nước rỉ b&atilde;i r&aacute;c. Cadimi c&oacute; thể xuất hiện trong đường ống th&eacute;p tr&aacute;ng kẽm nếu xảy ra hiện tượng ăn m&ograve;n.</p>\r\n\r\n<p>Cadimi c&oacute; t&aacute;c động xấu đến thận. Khi bị nhiễm độc cao c&oacute; khả năng g&acirc;y &oacute;i mữa. Ti&ecirc;u chuẩn nước uống quy định Cadimi nhỏ hơn 0,003 mg/l.</p>\r\n\r\n<p><strong>14. Cr&ocirc;m</strong></p>\r\n\r\n<p>Cr&ocirc;m c&oacute; mặt trong nguồn nước khi bị nhiễm nước thải c&ocirc;ng nghiệp khai th&aacute;c mỏ, xi mạ, thuộc da, thuốc nhuộm, sản xuất giấy v&agrave; gốm sứ.</p>\r\n\r\n<p>Cr&ocirc;m h&oacute;a trị 6 c&oacute; độc t&iacute;nh mạnh hơn Cr&ocirc;m h&oacute;a trị 3 v&agrave; t&aacute;c động xấu đến c&aacute;c bộ phận cơ thể như gan, thận, cơ quan h&ocirc; hấp. Nhiễm độc Cr&ocirc;m cấp t&iacute;nh c&oacute; thể g&acirc;y xuất huyết, vi&ecirc;m da, u nhọt. Cr&ocirc;m được xếp v&agrave;o chất độc nh&oacute;m 1 (c&oacute; khả năng g&acirc;y ung thư cho người v&agrave; vật nu&ocirc;i). Ti&ecirc;u chuẩn nước uống quy định cr&ocirc;m nhỏ hơn 0,05 mg/l.</p>\r\n\r\n<p><strong>15. Đồng</strong></p>\r\n\r\n<p>Đồng hiện diện trong nước do hiện tượng ăn m&ograve;n tr&ecirc;n đường ống v&agrave; c&aacute;c dụng cụ thiết bị l&agrave;m bằng đồng hoặc đồng thau. C&aacute;c loại h&oacute;a chất diệt tảo được sử dụng rộng r&atilde;i tr&ecirc;n ao hồ cũng l&agrave;m tăng h&agrave;m lượng đồng trong nguồn nước. Nước thải từ nh&agrave; m&aacute;y luyện kim, xi mạ, thuộc da, sản xuất thuốc trừ s&acirc;u, diệt cỏ hay phim ảnh cũng g&oacute;p phần l&agrave;m tăng lượng đồng trong nguồn nước.</p>\r\n\r\n<p>Đồng kh&ocirc;ng t&iacute;ch lũy trong cơ thể nhiều đến mức g&acirc;y độc. Ở h&agrave;m lượng 1 &ndash; 2 mg/l đ&atilde; l&agrave;m cho nước c&oacute; vị kh&oacute; chịu, v&agrave; kh&ocirc;ng thể uống được khi nồng độ cao từ 5 &ndash; 8 mg/l. Ti&ecirc;u chuẩn nước uống v&agrave; nước sạch đều quy định h&agrave;m lượng đồng nhỏ hơn 2 mg/l.</p>\r\n\r\n<p><strong>16. Ch&igrave;</strong></p>\r\n\r\n<p>Trong nguồn nước thi&ecirc;n nhi&ecirc;n chỉ ph&aacute;t hiện h&agrave;m lượng ch&igrave; 0,4 &ndash; 0,8 mg/l. Tuy nhi&ecirc;n do &ocirc; nhiễm nước thải c&ocirc;ng nghiệp hoặc hiện tượng ăn m&ograve;n đường ống n&ecirc;n c&oacute; thể ph&aacute;t hiện ch&igrave; trong nước uống ở mức độ cao hơn.<br />\r\nKhi h&agrave;m lượng ch&igrave; trong m&aacute;u cao c&oacute; thể g&acirc;y tổn thương n&atilde;o, rối loạn ti&ecirc;u h&oacute;a, yếu cơ, ph&aacute; hủy hồng cầu. Ch&igrave; c&oacute; thể t&iacute;ch lũy trong cơ thể đến mức cao v&agrave; g&acirc;y độc. Ti&ecirc;u chuẩn nước uống v&agrave; nước sạch đều quy định h&agrave;m lượng ch&igrave; nhỏ hơn 0,01 mg/l.</p>\r\n\r\n<p><strong>17. Kẽm</strong></p>\r\n\r\n<p>Kẽm &iacute;t khi c&oacute; trong nước, ngoại trừ bị &ocirc; nhiễm từ nguồn nước thải của c&aacute;c khu khai th&aacute;c quặng.</p>\r\n\r\n<p>Chưa ph&aacute;t hiện kẽm g&acirc;y độc cho cơ thể người, nhưng ở h&agrave;m lượng &gt; 5 mg/l đ&atilde; l&agrave;m cho nước c&oacute; m&agrave;u trắng sữa. Ti&ecirc;u chuẩn nước uống v&agrave; nước sạch đều quy định h&agrave;m lượng kẽm &lt; 3mg/l.</p>\r\n\r\n<p><strong>18. Niken</strong></p>\r\n\r\n<p>Niken &iacute;t khi hiện diện trong nước, ngoại trừ bị &ocirc; nhiễm từ nguồn nước thải của ng&agrave;nh điện tử, gốm sứ, ắc quy, sản xuất th&eacute;p.</p>\r\n\r\n<p>Niken c&oacute; độc t&iacute;nh thấp v&agrave; kh&ocirc;ng t&iacute;ch lũy trong c&aacute;c m&ocirc;. Ti&ecirc;u chuẩn nước uống v&agrave; nước sạch đều quy định h&agrave;m lượng niken nhỏ hơn 0,02mg/l.</p>\r\n\r\n<p><strong>19. Thủy ng&acirc;n</strong></p>\r\n\r\n<p>Thủy ng&acirc;n hiếm khi tồn tại trong nước. Tuy nhi&ecirc;n c&aacute;c muối thủy ng&acirc;n được d&ugrave;ng trong c&ocirc;ng nghệ khai kho&aacute;ng c&oacute; khả năng l&agrave;m &ocirc; nhiễm nguồn nước.</p>\r\n\r\n<p>Khi nhiễm độc thủy ng&acirc;n c&aacute;c cơ quan như thận v&agrave; hệ thần kinh sẽ bị rối loạn. Ti&ecirc;u chuẩn nước uống v&agrave; nước sạch đều quy định h&agrave;m lượng thủy ng&acirc;n nhỏ hơn 0,001 mg/l.</p>\r\n\r\n<p><strong>20. Molybden</strong></p>\r\n\r\n<p>Molybden &iacute;t khi c&oacute; mặt trong nước. Molybden thường c&oacute; trong nước thải ng&agrave;nh điện, h&oacute;a dầu, thủy tinh, gốm sứ v&agrave; thuốc nhuộm.</p>\r\n\r\n<p>Molybden dễ hấp thụ theo đường ti&ecirc;u h&oacute;a v&agrave; tấn c&ocirc;ng c&aacute;c cơ quan như gan, thận. Ti&ecirc;u chuẩn nước uống quy định molybden nhỏ hơn 0,07 mg/l.</p>\r\n\r\n<p><strong>21. Clorua</strong></p>\r\n\r\n<p>Nguồn nước c&oacute; h&agrave;m lượng clorua cao thường do hiện tượng thẩm thấu từ nước biển hoặc do &ocirc; nhiễm từ c&aacute;c lọai nước thải như mạ kẽm, khai th&aacute;c dầu, sản xuất giấy, sản xuất nước từ quy tr&igrave;nh l&agrave;m mềm.</p>\r\n\r\n<p>Clorua kh&ocirc;ng g&acirc;y hại cho sức khỏe. Giới hạn tối đa của clorua được lựa chọn theo h&agrave;m lượng natri trong nước, khi kết hợp với clorua sẽ g&acirc;y vị mặn kh&oacute; uống. Ti&ecirc;u chuẩn nước sạch quy định Clorua nhỏ hơn 300 mg/l. Ti&ecirc;u chuẩn nước uống quy định Clorua nhỏ hơn 250 mg/l.</p>\r\n\r\n<p><strong>22. Am&ocirc;ni &ndash; Nitrit &ndash; Nitrat</strong></p>\r\n\r\n<p>C&aacute;c dạng thường gặp trong nước của hợp chất nitơ l&agrave; am&ocirc;ni, nitrit, nitrat, l&agrave; kết quả của qu&aacute; tr&igrave;nh ph&acirc;n hủy c&aacute;c chất hữu cơ hoặc do &ocirc; nhiễm từ nước thải. Trong nh&oacute;m n&agrave;y, am&ocirc;ni l&agrave; chất g&acirc;y độc nhiều nhất cho c&aacute; v&agrave; c&aacute;c lo&agrave;i thủy sinh. Nitrit được h&igrave;nh th&agrave;nh từ phản ứng ph&acirc;n hủy nitơ hữu cơ v&agrave; am&ocirc;ni v&agrave; với sự tham gia của vi khuẩn. Sau đ&oacute; nitrit sẽ được oxy h&oacute;a th&agrave;nh nitrat. Ngo&agrave;i ra, nitrat c&ograve;n c&oacute; mặt trong nguồn nước l&agrave; do nước thải từ c&aacute;c ng&agrave;nh h&oacute;a chất, từ đồng ruộng c&oacute; sử dụng ph&acirc;n h&oacute;a học, nước rỉ b&atilde;i r&aacute;c, nước mưa chảy tr&agrave;n. Sự c&oacute; mặt hợp chất nitơ trong th&agrave;nh phần h&oacute;a học của nước cho thấy dấu hiệu &ocirc; nhiễm nguồn nước.</p>\r\n\r\n<p><strong>23. Sunfat</strong></p>\r\n\r\n<p>Sunfat thường c&oacute; mặt trong nước l&agrave; do qu&aacute; tr&igrave;nh oxy h&oacute;a c&aacute;c chất hữu cơ c&oacute; chứa sunfua hoặc do &ocirc; nhiễm từ nguồn nước thải ng&agrave;nh dệt nhuộm, thuộc da, luyện kim, sản xuất giấy. Nước nhiễm ph&egrave;n thường chứa h&agrave;m lượng sunfat cao.</p>\r\n\r\n<p>Ở nồng độ sunfat 200mg/l nước c&oacute; vị ch&aacute;t, h&agrave;m lượng cao hơn c&oacute; thể g&acirc;y bệnh ti&ecirc;u chảy.</p>\r\n\r\n<p>Ti&ecirc;u chuẩn nước uống quy định sunfat nhỏ hơn 250 mg/l.</p>\r\n\r\n<p><strong>24. Florua</strong></p>\r\n\r\n<p>Nước mặt thường c&oacute; h&agrave;m lượng flo thấp khoảng 0,2 mg/l. Đối với nước ngầm, khi chảy qua c&aacute;c tầng đ&aacute; v&ocirc;i, dolomit, đất s&eacute;t, h&agrave;m lượng flo trong nước c&oacute; thể cao đến 8 &ndash; 9 mg/l.</p>\r\n\r\n<p>Kết quả nghi&ecirc;n cứu cho thấy khi h&agrave;m lượng flo đạt 2 mg/l đ&atilde; l&agrave;m đen răng. Nếu sử dụng thường xuy&ecirc;n nguồn nước c&oacute; h&agrave;m lượng Flo cao hơn 4 mg/l c&oacute; thể l&agrave;m mục xương. Flo kh&ocirc;ng c&oacute; biểu hiện g&acirc;y ung thư. Ti&ecirc;u chuẩn nước uống quy định h&agrave;m lượng flo trong khoảng 0,7 &ndash; 1,5 mg/l.</p>\r\n\r\n<p><strong>25. Xyanua</strong></p>\r\n\r\n<p>Xyanua c&oacute; mặt trong nguồn nước do &ocirc; nhiễm từ c&aacute;c loại nước thải ng&agrave;nh nhựa, xi mạ, luyện kim, h&oacute;a chất, sợi tổng hợp.</p>\r\n\r\n<p>Xyanua rất độc, thường tấn c&ocirc;ng c&aacute;c cơ quan như phổi, da, đường ti&ecirc;u h&oacute;a. Ti&ecirc;u chuẩn nước uống v&agrave; nước sạch đều quy định h&agrave;m lượng xuanua nhỏ hơn 0,07 mg/l.</p>\r\n\r\n<p><strong>26. Coliform</strong></p>\r\n\r\n<p>Vi khuẩn Coliform (phổ biến l&agrave; Escherichia Coli) thường c&oacute; trong hệ ti&ecirc;u h&oacute;a của người. Sự ph&aacute;t hiện vi khuẩn Coli cho thấy nguồn nước đ&atilde; c&oacute; dấu hiệu &ocirc; nhiễm.</p>\r\n\r\n<p>Ti&ecirc;u chuẩn nước uống v&agrave; nước sạch đều quy định h&agrave;m lượng E. Coliform bằng 0. Ri&ecirc;ng Coliform tổng số trong nước sạch được cho ph&eacute;p 50 vi khuẩn / 100 ml.</p>\r\n\r\n<h3 style="text-align:justify"><strong>C&Aacute;C CHỈ TI&Ecirc;U L&Yacute; HO&Aacute; CỦA NƯỚC UỐNG Đ&Oacute;NG CHAI LI&Ecirc;N QUAN ĐẾN AN TO&Agrave;N THỰC PHẨM</strong></h3>\r\n\r\n<table border="1" style="border-collapse:collapse; border-spacing:0px; font-family:helvetica neue,helvetica,arial,sans-serif; line-height:18px; max-width:100%; vertical-align:top; width:auto">\r\n	<tbody>\r\n		<tr>\r\n			<td style="vertical-align:top">T&ecirc;n chỉ ti&ecirc;u</td>\r\n			<td style="vertical-align:top">\r\n			<p>Giới hạn</p>\r\n\r\n			<p>tối đa</p>\r\n			</td>\r\n			<td style="vertical-align:top">Phương ph&aacute;p thử</td>\r\n			<td style="vertical-align:top">\r\n			<p>Ph&acirc;n</p>\r\n\r\n			<p>loại</p>\r\n\r\n			<p>chỉ</p>\r\n\r\n			<p>ti&ecirc;u</p>\r\n\r\n			<p>(4)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">1. Antimony, mg/l</td>\r\n			<td style="vertical-align:top">0,02</td>\r\n			<td style="vertical-align:top">ISO 11885:2007; ISO 15586:2003; AOAC 964.16</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">2. Arsen, mg/l</td>\r\n			<td style="vertical-align:top">0,01</td>\r\n			<td style="vertical-align:top">TCVN 6626:2000 (ISO 11969:1996); ISO 11885:2007; ISO 15586:2003; AOAC 986.15</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">3. Bari, mg/l</td>\r\n			<td style="vertical-align:top">0,7</td>\r\n			<td style="vertical-align:top">ISO 11885:2007; AOAC 920.201</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">4. Bor, mg/l</td>\r\n			<td style="vertical-align:top">0,5</td>\r\n			<td style="vertical-align:top">TCVN 6635:2000 (ISO 9390:1990); ISO 11885:2007</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">5. Bromat, mg/l</td>\r\n			<td style="vertical-align:top">0,01</td>\r\n			<td style="vertical-align:top">ISO 15061:2001</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">6. Cadmi, mg/l</td>\r\n			<td style="vertical-align:top">0,003</td>\r\n			<td style="vertical-align:top">TCVN 6193:1996 (ISO 8288:1986); ISO 11885:2007; ISO 15586:2003; AOAC 974.27; AOAC 986.15</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">7. Clor, mg/l</td>\r\n			<td style="vertical-align:top">5</td>\r\n			<td style="vertical-align:top">ISO 7393-1:1985, ISO 7393-2:1985, ISO 7393-3:1990</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">8. Clorat, mg/l</td>\r\n			<td style="vertical-align:top">0,7</td>\r\n			<td style="vertical-align:top">TCVN 6494-4:2000 (ISO 10304-4:1997)</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">9. Clorit, mg/l</td>\r\n			<td style="vertical-align:top">0,7</td>\r\n			<td style="vertical-align:top">TCVN 6494-4:2000 (ISO 10304-4:1997)</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">10. Crom, mg/l</td>\r\n			<td style="vertical-align:top">0,05</td>\r\n			<td style="vertical-align:top">TCVN 6222:2008 (ISO 9174:1998); ISO 11885:2007; ISO 15586:2003</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">11. Đồng, mg/l</td>\r\n			<td style="vertical-align:top">2</td>\r\n			<td style="vertical-align:top">TCVN 6193:1996 (ISO 8288:1986); ISO 11885:2007; ISO 15586:2003; AOAC 960.40</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">12. Xyanid, mg/l</td>\r\n			<td style="vertical-align:top">0,07</td>\r\n			<td style="vertical-align:top">TCVN 6181:1996 (ISO 6703-1:1984); TCVN 7723:2007 (ISO 14403:2002)</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">13. Fluorid, mg/l</td>\r\n			<td style="vertical-align:top">1,5</td>\r\n			<td style="vertical-align:top">TCVN 6195:1996 (ISO 10359-1:1992); TCVN 6490:1999 (ISO 10359-2:1994); ISO 10304-1:2007</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">14. Ch&igrave;, mg/l</td>\r\n			<td style="vertical-align:top">0,01</td>\r\n			<td style="vertical-align:top">TCVN 6193:1996 (ISO 8288:1986); ISO 11885:2007; ISO 15586:2003; AOAC 974.27</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">15. Mangan, mg/l</td>\r\n			<td style="vertical-align:top">0,4</td>\r\n			<td style="vertical-align:top">TCVN 6002:1995 (ISO 6333:1986); ISO 11885:2007; ISO 15586:2003</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">16. Thủy ng&acirc;n, mg/l</td>\r\n			<td style="vertical-align:top">0,006</td>\r\n			<td style="vertical-align:top">TCVN 7877:2008 (ISO 5666:1999); AOAC 977.22</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">17. Molybden, mg/l</td>\r\n			<td style="vertical-align:top">0,07</td>\r\n			<td style="vertical-align:top">TCVN 7929:2008 (EN 14083:2003); ISO 11885:2007; ISO 15586:2003</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">18. Nickel, mg/l</td>\r\n			<td style="vertical-align:top">0,07</td>\r\n			<td style="vertical-align:top">TCVN 6193:1996 (ISO 8288:1986); ISO 11885:2007; ISO 15586:2003</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">19. Nitrat (5), mg/l</td>\r\n			<td style="vertical-align:top">50</td>\r\n			<td style="vertical-align:top">TCVN 6180:1996 (ISO 7890-3:1998); ISO 10304-1:2007</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">20. Nitrit (5), mg/l</td>\r\n			<td style="vertical-align:top">3</td>\r\n			<td style="vertical-align:top">TCVN 6178: 1996 (ISO 6777:1984); ISO 10304-1:2007</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">21. Selen, mg/l</td>\r\n			<td style="vertical-align:top">0,01</td>\r\n			<td style="vertical-align:top">TCVN 6183:1996 (ISO 9965:1993); ISO 11885:2007; ISO 15586:2003; AOAC 986.15</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">\r\n			<p>22. Mức nhiễm xạ:</p>\r\n\r\n			<p>- Hoạt độ ph&oacute;ng xạ &alpha;, Bq/l<br />\r\n			- Hoạt độ ph&oacute;ng xạ &beta;, Bq/l</p>\r\n			</td>\r\n			<td style="vertical-align:top"><br />\r\n			0,5<br />\r\n			1</td>\r\n			<td style="vertical-align:top"><br />\r\n			ISO 9696:2007<br />\r\n			ISO 9697:2008</td>\r\n			<td style="vertical-align:top">B</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ghi ch&uacute;:<br />\r\n(4) Chỉ ti&ecirc;u loại A: bắt buộc phải thử nghi&ecirc;̣m để đánh giá hợp quy. Chỉ ti&ecirc;u loại B: kh&ocirc;ng bắt buộc phải thử nghi&ecirc;̣m để đánh giá hợp quy nhưng t&ocirc;̉ chức, cá nh&acirc;n sản xu&acirc;́t, nh&acirc;̣p kh&acirc;̉u, chế biến c&aacute;c sản phẩm sữa dạng lỏng phải đ&aacute;p ứng c&aacute;c y&ecirc;u cầu đối với chỉ ti&ecirc;u loại B.<br />\r\n(5) Tỷ lệ nồng độ của mỗi chất so với giới hạn tối đa:</p>\r\n\r\n<p>C<span style="font-size:10px">nitrat</span>/GHTĐ<span style="font-size:10px">nitrat</span>&nbsp;+ C<span style="font-size:10px">nitrit</span>/GHTĐ<span style="font-size:10px">nitrit</span>&nbsp;&lt; 1.</p>\r\n\r\n<h3>C&Aacute;C CHỈ TI&Ecirc;U VI SINH VẬT CỦA NƯỚC KHO&Aacute;NG THI&Ecirc;N NHI&Ecirc;N Đ&Oacute;NG CHAI V&Agrave; NƯỚC UỐNG Đ&Oacute;NG CHAI</h3>\r\n\r\n<p>I. Kiểm tra lần đầu</p>\r\n\r\n<table border="1" style="border-collapse:collapse; border-spacing:0px; font-family:helvetica neue,helvetica,arial,sans-serif; line-height:18px; max-width:100%; vertical-align:top; width:auto">\r\n	<tbody>\r\n		<tr>\r\n			<td style="vertical-align:top">Chỉ ti&ecirc;u</td>\r\n			<td style="vertical-align:top">\r\n			<p>Lượng</p>\r\n\r\n			<p>mẫu</p>\r\n			</td>\r\n			<td style="vertical-align:top">Y&ecirc;u cầu</td>\r\n			<td style="vertical-align:top">Phương ph&aacute;p thử</td>\r\n			<td style="vertical-align:top">\r\n			<p>Ph&acirc;n</p>\r\n\r\n			<p>loại</p>\r\n\r\n			<p>chỉ</p>\r\n\r\n			<p>ti&ecirc;u</p>\r\n\r\n			<p>(6)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">1. E. coli hoặc coliform chịu nhiệt</td>\r\n			<td style="vertical-align:top">\r\n			<p>1x250</p>\r\n\r\n			<p>ml</p>\r\n			</td>\r\n			<td style="vertical-align:top">Kh&ocirc;ng ph&aacute;t hiện được trong bất kỳ mẫu n&agrave;o</td>\r\n			<td style="vertical-align:top">TCVN 6187-1:2009 (ISO 9308-1:2000, With Cor 1:2007)</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">2. Coliform tổng số</td>\r\n			<td style="vertical-align:top">\r\n			<p>1x250</p>\r\n\r\n			<p>ml</p>\r\n			</td>\r\n			<td rowspan="4" style="vertical-align:top">\r\n			<p>Nếu số vi khuẩn (b&agrave;o tử) &gt;1 v&agrave; &lt;2 th&igrave; tiến h&agrave;nh kiểm tra lần thứ hai.</p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p>Nếu số vi khuẩn (b&agrave;o tử) &gt;2 th&igrave; loại bỏ</p>\r\n			</td>\r\n			<td style="vertical-align:top">TCVN 6187-1:2009 (ISO 9308-1:2000, With Cor 1:2007)</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">3. Streptococci feacal</td>\r\n			<td style="vertical-align:top">\r\n			<p>1x250</p>\r\n\r\n			<p>ml</p>\r\n			</td>\r\n			<td style="vertical-align:top">ISO 7899-2:2000</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">4. Pseudomonas aeruginosa</td>\r\n			<td style="vertical-align:top">\r\n			<p>1x250</p>\r\n\r\n			<p>ml</p>\r\n			</td>\r\n			<td style="vertical-align:top">ISO 16266:2006</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">5. B&agrave;o tử vi khuẩn kị kh&iacute; khử sulfit</td>\r\n			<td style="vertical-align:top">\r\n			<p>1x50</p>\r\n\r\n			<p>ml</p>\r\n			</td>\r\n			<td style="vertical-align:top">TCVN 6191-2:1996 (ISO 6461-2:1986)</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>II. Kiểm tra lần thứ hai</p>\r\n\r\n<table border="1" style="border-collapse:collapse; border-spacing:0px; font-family:helvetica neue,helvetica,arial,sans-serif; line-height:18px; max-width:100%; vertical-align:top; width:auto">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan="2" style="vertical-align:top">T&ecirc;n chỉ ti&ecirc;u</td>\r\n			<td colspan="2" style="vertical-align:top">\r\n			<p>Kế hoạch</p>\r\n\r\n			<p>lấy mẫu</p>\r\n			</td>\r\n			<td colspan="2" style="vertical-align:top">Giới hạn</td>\r\n			<td rowspan="2" style="vertical-align:top">Phương ph&aacute;p thử</td>\r\n			<td rowspan="2" style="vertical-align:top">\r\n			<p>Ph&acirc;n</p>\r\n\r\n			<p>loại</p>\r\n\r\n			<p>chỉ</p>\r\n\r\n			<p>ti&ecirc;u</p>\r\n\r\n			<p>(6)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">n (7)</td>\r\n			<td style="vertical-align:top">c (8)</td>\r\n			<td style="vertical-align:top">m (9)</td>\r\n			<td style="vertical-align:top">M (10)</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">1. Coliform tổng số</td>\r\n			<td style="vertical-align:top">4</td>\r\n			<td style="vertical-align:top">1</td>\r\n			<td style="vertical-align:top">0</td>\r\n			<td style="vertical-align:top">2</td>\r\n			<td style="vertical-align:top">TCVN 6187-1:2009 (ISO 9308-1:2000, With Cor 1:2007)</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">2. Streptococci feacal</td>\r\n			<td style="vertical-align:top">4</td>\r\n			<td style="vertical-align:top">1</td>\r\n			<td style="vertical-align:top">0</td>\r\n			<td style="vertical-align:top">2</td>\r\n			<td style="vertical-align:top">ISO 7899-2:2000</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">3. Pseudomonas aeruginosa</td>\r\n			<td style="vertical-align:top">4</td>\r\n			<td style="vertical-align:top">1</td>\r\n			<td style="vertical-align:top">0</td>\r\n			<td style="vertical-align:top">2</td>\r\n			<td style="vertical-align:top">ISO 16266:2006</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="vertical-align:top">4. B&agrave;o tử vi khuẩn kị kh&iacute; khử sulfit</td>\r\n			<td style="vertical-align:top">4</td>\r\n			<td style="vertical-align:top">1</td>\r\n			<td style="vertical-align:top">0</td>\r\n			<td style="vertical-align:top">2</td>\r\n			<td style="vertical-align:top">TCVN 6191-2:1996 (ISO 6461-2:1986)</td>\r\n			<td style="vertical-align:top">A</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Ghi ch&uacute;:</p>\r\n\r\n<p>(6) Chỉ ti&ecirc;u loại A: bắt buộc phải thử nghi&ecirc;̣m để đánh giá hợp quy.</p>\r\n\r\n<p>(7) n: số đơn vị mẫu được lấy từ l&ocirc; h&agrave;ng cần kiểm tra.</p>\r\n\r\n<p>(8) c: số đơn vị mẫu tối đa c&oacute; thể chấp nhận hoặc số đơn vị mẫu tối đa cho ph&eacute;p vượt qu&aacute; chỉ ti&ecirc;u vi sinh vật m. Nếu vượt qu&aacute; số đơn vị mẫu n&agrave;y th&igrave; l&ocirc; h&agrave;ng được coi l&agrave; kh&ocirc;ng đạt.</p>\r\n\r\n<p>(9) m: số lượng hoặc mức tối đa vi khuẩn c&oacute; trong 1 gam sản phẩm; c&aacute;c gi&aacute; trị vượt qu&aacute; mức n&agrave;y th&igrave; c&oacute; thể được chấp nhận hoặc kh&ocirc;ng được chấp nhận.</p>\r\n\r\n<p>(10) M: l&agrave; mức vi sinh vật tối đa được d&ugrave;ng để ph&acirc;n định giữa chất lượng sản phẩm c&oacute; thể đạt v&agrave; kh&ocirc;ng đạt.</p>\r\n', 'admin', '2015-07-16 18:28:41', 1, 'tieu-chi-nuoc-sach.jpg');

